/**
 * Manages the Git Graph Extension State, which stores data in both the Visual Studio Code Global & Workspace State.
 */
export class ExtensionState extends Disposable {
	private readonly globalState: vscode.Memento;
	private readonly workspaceState: vscode.Memento;
	private readonly globalStoragePath: string;
	private avatarStorageAvailable: boolean = false;

	/**
	 * Creates the Git Graph Extension State.
	 * @param context The context of the extension.
	 * @param onDidChangeGitExecutable The Event emitting the Git executable for Git Graph to use.
	 */
	constructor(context: vscode.ExtensionContext, onDidChangeGitExecutable: Event<GitExecutable>) {
		super();
		this.globalState = context.globalState;
		this.workspaceState = context.workspaceState;

		this.globalStoragePath = getPathFromStr(context.globalStoragePath);
		fs.stat(this.globalStoragePath + AVATAR_STORAGE_FOLDER, (err) => {
			if (!err) {
				this.avatarStorageAvailable = true;
			} else {
				fs.mkdir(this.globalStoragePath, () => {
					fs.mkdir(this.globalStoragePath + AVATAR_STORAGE_FOLDER, (err) => {
						if (!err || err.code === 'EEXIST') {
							// The directory was created, or it already exists
							this.avatarStorageAvailable = true;
						}
					});
				});
			}
		});

		this.registerDisposable(
			onDidChangeGitExecutable((gitExecutable) => {
				this.setLastKnownGitPath(gitExecutable.path);
			})
		);
	}

package strategy

import (
	"encoding/json"
	"fmt"
	"sort"
	"strconv"
	"strings"
	"time"

	"github.com/entireio/cli/cmd/entire/cli/checkpoint"
	"github.com/entireio/cli/cmd/entire/cli/checkpoint/id"
	"github.com/entireio/cli/cmd/entire/cli/paths"

	"github.com/go-git/go-git/v5"
)

// NoDescription is the default description for sessions without one.
const NoDescription = "No description"

// Session represents a Claude Code session with its checkpoints.
// A session is created when a user runs `claude` and tracks all changes
// made during that interaction.
type Session struct {
	// ID is the unique session identifier (e.g., "2025-12-01-8f76b0e8-b8f1-4a87-9186-848bdd83d62e")
	ID string

	// Description is a human-readable summary of the session
	// (typically the first prompt or derived from commit messages)
	Description string

	// Strategy is the name of the strategy that created this session
	Strategy string

	// StartTime is when the session was started
	StartTime time.Time

	// Checkpoints is the list of save points within this session
	Checkpoints []Checkpoint
}

// Checkpoint represents a save point within a session.
// Checkpoints can be either session-level (on Stop) or task-level (on subagent completion).
type Checkpoint struct {
	// CheckpointID is the stable 12-hex-char identifier for this checkpoint.
	// Used to look up metadata at <id[:2]>/<id[2:]>/ on entire/checkpoints/v1 branch.
	CheckpointID id.CheckpointID

	// Message is the commit message or checkpoint description
	Message string

	// Timestamp is when this checkpoint was created
	Timestamp time.Time

	// IsTaskCheckpoint indicates if this is a task checkpoint (vs a session checkpoint)
	IsTaskCheckpoint bool

	// ToolUseID is the tool use ID for task checkpoints (empty for session checkpoints)
	ToolUseID string
}

// PromptResponse represents a single user prompt and assistant response pair.
type PromptResponse struct {
	// Prompt is the user's message
	Prompt string

	// Response is the assistant's response
	Response string

	// Files is the list of files modified during this prompt/response
	Files []string
}

// CheckpointDetails contains detailed information extracted from a checkpoint's transcript.
// This is used by the explain command to display checkpoint content.
type CheckpointDetails struct {
	// Interactions contains all prompt/response pairs in this checkpoint.
	// For strategies like auto-commit/commit, this typically has one entry.
	// For strategies like shadow, this may have multiple entries.
	Interactions []PromptResponse

	// Files is the aggregate list of all files modified in this checkpoint.
	// This is a convenience field that combines files from all interactions.
	Files []string
}

// ListSessions returns all sessions from the entire/checkpoints/v1 branch,
// plus any additional sessions from strategies implementing SessionSource.
// It automatically discovers all registered strategies and merges their sessions.
func ListSessions() ([]Session, error) {
	repo, err := OpenRepository()
	if err != nil {
		return nil, fmt.Errorf("failed to open git repository: %w", err)
	}

	// Get checkpoints from the entire/checkpoints/v1 branch
	checkpoints, err := ListCheckpoints()
	if err != nil {
		return nil, fmt.Errorf("failed to list checkpoints: %w", err)
	}

	// Group checkpoints by session ID
	// For multi-session checkpoints, expand SessionIDs array so each session gets the checkpoint
	sessionMap := make(map[string]*Session)
	for _, cp := range checkpoints {
		// Determine which session IDs this checkpoint belongs to
		// Multi-session checkpoints have SessionIDs populated; single-session use SessionID
		sessionIDs := cp.SessionIDs
		if len(sessionIDs) == 0 {
			sessionIDs = []string{cp.SessionID}
		}

		for _, sessionID := range sessionIDs {
			if sessionID == "" {
				continue
			}

			if existing, ok := sessionMap[sessionID]; ok {
				existing.Checkpoints = append(existing.Checkpoints, Checkpoint{
					CheckpointID:     cp.CheckpointID,
					Message:          "Checkpoint: " + cp.CheckpointID.String(),
					Timestamp:        cp.CreatedAt,
					IsTaskCheckpoint: cp.IsTask,
					ToolUseID:        cp.ToolUseID,
				})
			} else {
				// Get description from the checkpoint tree
				description := getDescriptionForCheckpoint(repo, cp.CheckpointID)

				sessionMap[sessionID] = &Session{
					ID:          sessionID,
					Description: description,
					Strategy:    "", // Will be set from metadata if available
					StartTime:   cp.CreatedAt,
					Checkpoints: []Checkpoint{{
						CheckpointID:     cp.CheckpointID,
						Message:          "Checkpoint: " + cp.CheckpointID.String(),
						Timestamp:        cp.CreatedAt,
						IsTaskCheckpoint: cp.IsTask,
						ToolUseID:        cp.ToolUseID,
					}},
				}
			}
		}
	}

	// Check all registered strategies for additional sessions
	for _, name := range List() {
		strat, stratErr := Get(name)
		if stratErr != nil {
			continue
		}
		source, ok := strat.(SessionSource)
		if !ok {
			continue
		}
		additionalSessions, addErr := source.GetAdditionalSessions()
		if addErr != nil {
			continue // Skip strategies that fail to provide additional sessions
		}
		for _, addSession := range additionalSessions {
			if addSession == nil {
				continue
			}
			if existing, ok := sessionMap[addSession.ID]; ok {
				// Merge checkpoints - deduplicate by CheckpointID
				existingCPIDs := make(map[string]bool)
				for _, cp := range existing.Checkpoints {
					existingCPIDs[cp.CheckpointID.String()] = true
				}
				for _, cp := range addSession.Checkpoints {
					if !existingCPIDs[cp.CheckpointID.String()] {
						existing.Checkpoints = append(existing.Checkpoints, cp)
					}
				}
				// Update start time if additional session is older
				if addSession.StartTime.Before(existing.StartTime) {
					existing.StartTime = addSession.StartTime
				}
				// Use description from additional source if existing is empty
				if existing.Description == "" || existing.Description == NoDescription {
					existing.Description = addSession.Description
				}
			} else {
				// New session from additional source
				sessionMap[addSession.ID] = addSession
			}
		}
	}

	// Convert map to slice
	sessions := make([]Session, 0, len(sessionMap))
	for _, session := range sessionMap {
		// Sort checkpoints within each session by timestamp (most recent first)
		sort.Slice(session.Checkpoints, func(i, j int) bool {
			return session.Checkpoints[i].Timestamp.After(session.Checkpoints[j].Timestamp)
		})
		sessions = append(sessions, *session)
	}

	// Sort sessions by start time (most recent first)
	sort.Slice(sessions, func(i, j int) bool {
		return sessions[i].StartTime.After(sessions[j].StartTime)
	})

	return sessions, nil
}

// GetSession finds a session by ID (supports prefix matching).
// Returns ErrNoSession if no matching session is found.
func GetSession(sessionID string) (*Session, error) {
	sessions, err := ListSessions()
	if err != nil {
		return nil, err
	}
	return findSessionByID(sessions, sessionID)
}

// getDescriptionForCheckpoint reads the description for a checkpoint from the entire/checkpoints/v1 branch.
// It reads from the latest session subdirectory in the new storage format.
func getDescriptionForCheckpoint(repo *git.Repository, checkpointID id.CheckpointID) string {
	tree, err := GetMetadataBranchTree(repo)
	if err != nil {
		return NoDescription
	}

	// Get the checkpoint tree
	checkpointTree, err := tree.Tree(checkpointID.Path())
	if err != nil {
		return NoDescription
	}

	// Read root metadata.json to get session count and sessions map
	metadataFile, err := checkpointTree.File(paths.MetadataFileName)
	if err != nil {
		return NoDescription
	}

	content, err := metadataFile.Contents()
	if err != nil {
		return NoDescription
	}

	var summary checkpoint.CheckpointSummary
	if err := json.Unmarshal([]byte(content), &summary); err != nil {
		return NoDescription
	}

	// Find the first session's prompt/context path
	// Try to use the latest session for description (0-based indexing)
	sessionDir := "0"
	if len(summary.Sessions) > 0 {
		sessionDir = strconv.Itoa(len(summary.Sessions) - 1) // Use latest session
	}

	sessionTree, err := checkpointTree.Tree(sessionDir)
	if err != nil {
		return NoDescription
	}

	return getSessionDescriptionFromTree(sessionTree, "")
}

// findSessionByID finds a session by exact ID or prefix match.
func findSessionByID(sessions []Session, sessionID string) (*Session, error) {
	for _, session := range sessions {
		if session.ID == sessionID || strings.HasPrefix(session.ID, sessionID) {
			return &session, nil
		}
	}
	return nil, ErrNoSession
}

package agent

import (
	"fmt"
	"slices"
	"sync"
)

var (
	registryMu sync.RWMutex
	registry   = make(map[AgentName]Factory)
)

// Factory creates a new agent instance
type Factory func() Agent

// Register adds an agent factory to the registry.
// Called from init() in each agent implementation.
func Register(name AgentName, factory Factory) {
	registryMu.Lock()
	defer registryMu.Unlock()
	registry[name] = factory
}

// Get retrieves an agent by name.
//

func Get(name AgentName) (Agent, error) {
	registryMu.RLock()
	defer registryMu.RUnlock()

	factory, ok := registry[name]
	if !ok {
		return nil, fmt.Errorf("unknown agent: %s (available: %v)", name, List())
	}
	return factory(), nil
}

// List returns all registered agent names in sorted order.
func List() []AgentName {
	registryMu.RLock()
	defer registryMu.RUnlock()

	names := make([]AgentName, 0, len(registry))
	for name := range registry {
		names = append(names, name)
	}
	slices.Sort(names)
	return names
}

// Detect attempts to auto-detect which agent is being used.
// Checks each registered agent's DetectPresence method.
//

func Detect() (Agent, error) {
	registryMu.RLock()
	defer registryMu.RUnlock()

	for _, factory := range registry {
		ag := factory()
		if present, err := ag.DetectPresence(); err == nil && present {
			return ag, nil
		}
	}
	return nil, fmt.Errorf("no agent detected (available: %v)", List())
}

// AgentName is the registry key type for agents (e.g., "claude-code", "gemini").
//
//nolint:revive // stuttering is intentional - distinguishes from AgentType when both are used
type AgentName string

// AgentType is the display name type stored in metadata/trailers (e.g., "Claude Code", "Gemini CLI").
//
//nolint:revive // stuttering is intentional - distinguishes from AgentName when both are used
type AgentType string

// Agent name constants (registry keys)
const (
	AgentNameClaudeCode AgentName = "claude-code"
	AgentNameGemini     AgentName = "gemini"
)

// Agent type constants (type identifiers stored in metadata/trailers)
const (
	AgentTypeClaudeCode AgentType = "Claude Code"
	AgentTypeGemini     AgentType = "Gemini CLI"
	AgentTypeUnknown    AgentType = "Agent" // Fallback for backwards compatibility
)

// DefaultAgentName is the registry key for the default agent.
const DefaultAgentName AgentName = AgentNameClaudeCode

// GetByAgentType retrieves an agent by its type identifier.
//
// Note: This uses a linear search that instantiates agents until a match is found.
// This is acceptable because:
//   - Agent count is small (~2-20 agents)
//   - Agent factories are lightweight (empty struct allocation)
//   - Called infrequently (commit hooks, rewind, debug commands - not hot paths)
//   - Cost is ~400ns worst case vs milliseconds for I/O operations
//
// Only optimize if agent count exceeds 100 or profiling shows this as a bottleneck.
func GetByAgentType(agentType AgentType) (Agent, error) {
	registryMu.RLock()
	defer registryMu.RUnlock()

	for _, factory := range registry {
		ag := factory()
		if ag.Type() == agentType {
			return ag, nil
		}
	}

	return nil, fmt.Errorf("unknown agent type: %s", agentType)
}

// AllProtectedDirs returns the union of ProtectedDirs from all registered agents.
func AllProtectedDirs() []string {
	// Copy factories under the lock, then release before calling external code.
	registryMu.RLock()
	factories := make([]Factory, 0, len(registry))
	for _, f := range registry {
		factories = append(factories, f)
	}
	registryMu.RUnlock()

	seen := make(map[string]struct{})
	var dirs []string
	for _, factory := range factories {
		for _, d := range factory().ProtectedDirs() {
			if _, ok := seen[d]; !ok {
				seen[d] = struct{}{}
				dirs = append(dirs, d)
			}
		}
	}
	slices.Sort(dirs)
	return dirs
}

// Default returns the default agent.
// Returns nil if the default agent is not registered.
//
//nolint:errcheck // Factory pattern returns interface; error is acceptable to ignore for default
func Default() Agent {
	a, _ := Get(DefaultAgentName)
	return a
}

// Package agent provides interfaces and types for integrating with coding agents.
// It abstracts agent-specific behavior (hooks, log parsing, session storage) so that
// the same Strategy implementations can work with any coding agent.
package agent

import (
	"io"
)

// Agent defines the interface for interacting with a coding agent.
// Each agent implementation (Claude Code, Cursor, Aider, etc.) converts its
// native format to the normalized types defined in this package.
type Agent interface {
	// Name returns the agent registry key (e.g., "claude-code", "gemini")
	Name() AgentName

	// Type returns the agent type identifier (e.g., "Claude Code", "Gemini CLI")
	// This is stored in metadata and trailers.
	Type() AgentType

	// Description returns a human-readable description for UI
	Description() string

	// DetectPresence checks if this agent is configured in the repository
	DetectPresence() (bool, error)

	// GetHookConfigPath returns path to hook config file (empty if none)
	GetHookConfigPath() string

	// SupportsHooks returns true if agent supports lifecycle hooks
	SupportsHooks() bool

	// ParseHookInput parses hook callback input from stdin
	ParseHookInput(hookType HookType, reader io.Reader) (*HookInput, error)

	// GetSessionID extracts session ID from hook input
	GetSessionID(input *HookInput) string

	// TransformSessionID converts agent session ID to Entire session ID
	TransformSessionID(agentSessionID string) string

	// ExtractAgentSessionID extracts agent session ID from Entire ID
	ExtractAgentSessionID(entireSessionID string) string

	// ProtectedDirs returns repo-root-relative directories that should never be
	// modified or deleted during rewind or other destructive operations.
	// Examples: [".claude"] for Claude, [".gemini"] for Gemini.
	ProtectedDirs() []string

	// GetSessionDir returns where agent stores session data for this repo.
	// Examples:
	//   Claude: ~/.claude/projects/<sanitized-repo-path>/
	//   Aider: current working directory (returns repoPath)
	//   Cursor: ~/Library/Application Support/Cursor/User/globalStorage/
	GetSessionDir(repoPath string) (string, error)

	// ResolveSessionFile returns the path to the session transcript file for a given
	// agent session ID. Agents use different naming conventions:
	//   Claude: <sessionDir>/<id>.jsonl
	//   Gemini: <sessionDir>/session-<date>-<shortid>.json (searches for existing file)
	// If no existing file is found, returns a sensible default path.
	ResolveSessionFile(sessionDir, agentSessionID string) string

	// ReadSession reads session data from agent's storage.
	// Handles different formats: JSONL (Claude), SQLite (Cursor), Markdown (Aider)
	ReadSession(input *HookInput) (*AgentSession, error)

	// WriteSession writes session data for resumption.
	// Agent handles format conversion (JSONL, SQLite, etc.)
	WriteSession(session *AgentSession) error

	// FormatResumeCommand returns command to resume a session
	FormatResumeCommand(sessionID string) string
}

// HookSupport is implemented by agents with lifecycle hooks.
// This optional interface allows agents like Claude Code and Cursor to
// install and manage hooks that notify Entire of agent events.
type HookSupport interface {
	Agent

	// InstallHooks installs agent-specific hooks.
	// If localDev is true, hooks point to local development build.
	// If force is true, removes existing Entire hooks before installing.
	// Returns the number of hooks installed.
	InstallHooks(localDev bool, force bool) (int, error)

	// UninstallHooks removes installed hooks
	UninstallHooks() error

	// AreHooksInstalled checks if hooks are currently installed
	AreHooksInstalled() bool

	// GetSupportedHooks returns the hook types this agent supports
	GetSupportedHooks() []HookType
}

// HookHandler is implemented by agents that define their own hook vocabulary.
// Each agent defines its own hook names (verbs) which become subcommands
// under `entire hooks <agent>`. The actual handling is done by handlers
// registered in the CLI package to avoid circular dependencies.
//
// This allows different agents to have completely different hook vocabularies
// (e.g., Claude Code has "stop", Cursor might have "completion").
type HookHandler interface {
	Agent

	// GetHookNames returns the hook verbs this agent supports.
	// These are the subcommand names that will appear under `entire hooks <agent>`.
	// e.g., ["stop", "user-prompt-submit", "pre-task", "post-task", "post-todo"]
	GetHookNames() []string
}

// FileWatcher is implemented by agents that use file-based detection.
// Agents like Aider that don't support hooks can use file watching
// to detect session activity.
type FileWatcher interface {
	Agent

	// GetWatchPaths returns paths to watch for session changes
	GetWatchPaths() ([]string, error)

	// OnFileChange handles a detected file change and returns session info
