/* ── Reset & Base ── */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: var(--vscode-font-family);
  font-size: var(--vscode-font-size);
  color: var(--vscode-foreground);
  background: var(--vscode-editor-background);
  overflow: hidden;
}

#root {
  display: flex;
  flex-direction: column;
  height: 100vh;
}

/* ── Active Session Banner ── */
.active-banner {
  background: var(--vscode-statusBar-debuggingBackground, #388a34);
  color: var(--vscode-statusBar-debuggingForeground, #fff);
  padding: 6px 12px;
  font-size: 12px;
  flex-shrink: 0;
}

.active-session-line {
  display: flex;
  align-items: center;
  gap: 6px;
}

.active-dot {
  display: inline-block;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: #4caf50;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.4; }
}

/* ── Main Layout ── */
.main-layout {
  display: flex;
  flex: 1;
  overflow: hidden;
}

.left-pane {
  flex: 1;
  overflow-y: auto;
  min-width: 0;
  padding: 8px 0;
}

.detail-pane {
  width: 340px;
  flex-shrink: 0;
  border-left: 1px solid var(--vscode-panel-border);
  overflow-y: auto;
  padding: 12px;
}

/* ── Session List ── */
.session-list {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.session-group {
  margin-bottom: 2px;
}

.session-header {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 6px 12px;
  font-size: 12px;
  color: var(--vscode-descriptionForeground);
  border-bottom: 1px solid var(--vscode-panel-border);
  position: sticky;
  top: 0;
  background: var(--vscode-editor-background);
  z-index: 1;
}

.agent-badge {
  display: inline-block;
  background: var(--vscode-editorInfo-foreground, #3794ff);
  color: #fff;
  padding: 1px 8px;
  border-radius: 10px;
  font-size: 11px;
  font-weight: 500;
  flex-shrink: 0;
}

.session-id {
  font-family: var(--vscode-editor-font-family, monospace);
  font-size: 11px;
  color: var(--vscode-textLink-foreground);
  flex-shrink: 0;
}

.session-date {
  flex-shrink: 0;
}

.checkpoint-count {
  margin-left: auto;
  flex-shrink: 0;
}

/* ── Checkpoint Rows ── */
.checkpoint-list {
  display: flex;
  flex-direction: column;
}

.checkpoint-row {
  display: flex;
  align-items: flex-start;
  padding: 6px 12px 6px 16px;
  cursor: pointer;
  gap: 8px;
  min-height: 36px;
}

.checkpoint-row:hover {
  background: var(--vscode-list-hoverBackground);
}

.checkpoint-row.selected {
  background: var(--vscode-list-activeSelectionBackground);
  color: var(--vscode-list-activeSelectionForeground);
}

.checkpoint-dot {
  display: inline-block;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: var(--vscode-editorInfo-foreground, #3794ff);
  flex-shrink: 0;
  margin-top: 5px;
}

.checkpoint-info {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.checkpoint-message {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-size: 13px;
}

.checkpoint-meta {
  font-size: 11px;
  color: var(--vscode-descriptionForeground);
}

.checkpoint-hash {
  font-family: var(--vscode-editor-font-family, monospace);
  font-size: 11px;
  color: var(--vscode-textLink-foreground);
  flex-shrink: 0;
  margin-top: 3px;
}

/* ── Empty & Notice States ── */
.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
