  1→import * as fs from "fs";
  2→import * as path from "path";
  3→import { gitLog, gitShow, gitLsTree } from "./gitReader";
  4→import type {
  5→  GitCommit,
  6→  ActiveSession,
  7→  CommitDetail,
  8→  EntireCheckpoint,
  9→  RootCheckpointMetadata,
 10→  SessionGroup,
 11→  SessionMetadata,
 12→  TaskCheckpoint,
 13→} from "./types";
 14→
 15→const ORPHAN_REF = "entire/checkpoints/v1";
 16→
 17→export class DataProvider {
 18→  constructor(private cwd: string) {}
 19→
 20→  /** Parse git log output into structured commits. */
 21→  async getCommits(maxCount = 200): Promise<GitCommit[]> {
 22→    const raw = await gitLog(this.cwd, maxCount);
 23→    const records = raw.split("\x01").filter((r) => r.trim());
 24→
 25→    return records.map((record) => {
 26→      const fields = record.trim().split("\x00");
 27→      const [
 28→        hash,
 29→        abbreviatedHash,
 30→        parentStr,
 31→        author,
 32→        authorEmail,
 33→        date,
 34→        subject,
 35→        refStr,
 36→        checkpointTrailer,
 37→        attributionTrailer,
 38→        sessionTrailer,
 39→        agentTrailer,
 40→      ] = fields;
 41→
 42→      const parents = parentStr ? parentStr.split(" ").filter(Boolean) : [];
 43→      const refs = refStr
 44→        ? refStr.split(",").map((r) => r.trim()).filter(Boolean)
 45→        : [];
 46→
 47→      return {
 48→        hash,
 49→        abbreviatedHash,
 50→        parents,
 51→        author,
 52→        authorEmail,
 53→        date,
 54→        subject,
 55→        refs,
 56→        entireCheckpointId: checkpointTrailer?.trim() || undefined,
 57→        entireAttribution: attributionTrailer?.trim() || undefined,
 58→        entireSessionId: sessionTrailer?.trim() || undefined,
 59→        entireAgent: agentTrailer?.trim() || undefined,
 60→      };
 61→    });
 62→  }
 63→
 64→  /** Group Entire-annotated commits by session ID. */
 65→  async getSessions(maxCount = 200): Promise<SessionGroup[]> {
 66→    const commits = await this.getCommits(maxCount);
 67→    const entireCommits = commits.filter((c) => c.entireSessionId);
 68→
 69→    const groupMap = new Map<string, GitCommit[]>();
 70→    for (const commit of entireCommits) {
 71→      const sid = commit.entireSessionId!;
 72→      const group = groupMap.get(sid) ?? [];
 73→      group.push(commit);
 74→      groupMap.set(sid, group);
 75→    }
 76→
 77→    const sessions: SessionGroup[] = [];
 78→    for (const [sessionId, checkpoints] of groupMap) {
 79→      // Checkpoints are already in reverse chronological order from git log
 80→      const agent = checkpoints[0].entireAgent ?? "AI";
 81→      const dates = checkpoints.map((c) => new Date(c.date).getTime());
 82→      sessions.push({
 83→        sessionId,
 84→        agent,
 85→        checkpoints,
 86→        startedAt: new Date(Math.min(...dates)).toISOString(),
 87→        lastActivityAt: new Date(Math.max(...dates)).toISOString(),
 88→      });
 89→    }
 90→
 91→    // Sort sessions by most recent activity first
 92→    sessions.sort(
 93→      (a, b) =>
 94→        new Date(b.lastActivityAt).getTime() -
 95→        new Date(a.lastActivityAt).getTime()
 96→    );
 97→
 98→    return sessions;
 99→  }
100→
101→  /** Load full detail for a checkpoint. */
102→  async getCommitDetailByCheckpoint(checkpointId: string): Promise<CommitDetail> {
103→    const commits = await this.getCommits();
104→    const commit = commits.find((c) => c.entireCheckpointId === checkpointId);
105→    if (!commit) {
106→      throw new Error(`Checkpoint ${checkpointId} not found`);
107→    }
108→
109→    let checkpoint: EntireCheckpoint | undefined;
110→    try {
111→      checkpoint = await this.getCheckpointDetail(checkpointId);
112→    } catch {
113→      // Checkpoint data may not be available
114→    }
115→
116→    return { commit, checkpoint };
117→  }
118→
119→  /** Read checkpoint metadata from the orphan branch. */
120→  async getCheckpointDetail(checkpointId: string): Promise<EntireCheckpoint> {
121→    const shard = checkpointId.slice(0, 2);
122→    const rest = checkpointId.slice(2);
123→    const basePath = `${shard}/${rest}`;
124→
125→    // Read root metadata
126→    const rootRaw = await gitShow(this.cwd, ORPHAN_REF, `${basePath}/metadata.json`);
127→    const root: RootCheckpointMetadata = JSON.parse(rootRaw);
128→
129→    // Read session metadata for each session
130→    const sessions: SessionMetadata[] = [];
131→    for (const sess of root.sessions) {
132→      try {
133→        // metadata path starts with /, strip it
134→        const metaPath = sess.metadata.startsWith("/")
135→          ? sess.metadata.slice(1)
136→          : sess.metadata;
137→        const sessRaw = await gitShow(this.cwd, ORPHAN_REF, metaPath);
138→        sessions.push(JSON.parse(sessRaw));
139→      } catch {
140→        // Skip unreadable sessions
141→      }
142→    }
143→
144→    // Read task checkpoints
145→    const tasks = await this.getTaskCheckpoints(checkpointId);
146→
147→    return {
148→      checkpoint_id: root.checkpoint_id,
149→      strategy: root.strategy,
150→      branch: root.branch,
151→      sessions,
152→      tasks,
153→    };
154→  }
155→
156→  /** Discover and read task checkpoint files. */
157→  private async getTaskCheckpoints(checkpointId: string): Promise<TaskCheckpoint[]> {
158→    const shard = checkpointId.slice(0, 2);
159→    const rest = checkpointId.slice(2);
160→    const tasksPath = `${shard}/${rest}/tasks`;
161→
162→    try {
163→      const lsOutput = await gitLsTree(this.cwd, ORPHAN_REF, `${tasksPath}/`);
164→      const taskDirs = lsOutput.trim().split("\n").filter(Boolean);
165→
166→      const tasks: TaskCheckpoint[] = [];
167→      for (const dir of taskDirs) {
168→        // dir is like "3a/96b1501cdd/tasks/toolu_xxx"
169→        const dirName = dir.trim();
170→        try {
171→          const raw = await gitShow(
172→            this.cwd,
173→            ORPHAN_REF,
174→            `${dirName}/checkpoint.json`
175→          );
176→          tasks.push(JSON.parse(raw));
177→        } catch {
178→          // Skip unreadable tasks
179→        }
180→      }
181→      return tasks;
182→    } catch {
183→      return [];
184→    }
185→  }
186→
187→  /** Read active sessions from .git/entire-sessions/. */
188→  async getActiveSessions(): Promise<ActiveSession[]> {
189→    const sessionsDir = path.join(this.cwd, ".git", "entire-sessions");
190→
191→    try {
192→      const files = fs.readdirSync(sessionsDir).filter((f) => f.endsWith(".json"));
193→      const sessions: ActiveSession[] = [];
194→
