// â”€â”€ Git Data â”€â”€

export interface GitCommit {
  hash: string;
  abbreviatedHash: string;
  parents: string[];
  author: string;
  authorEmail: string;
  date: string; // ISO 8601
  subject: string;
  /** Refs like branch names, tags */
  refs: string[];
  /** Entire trailers parsed from commit */
  entireCheckpointId?: string;
  entireAttribution?: string;
  entireSessionId?: string;
  entireAgent?: string;
}

// â”€â”€ Entire Data â”€â”€

export interface TokenUsage {
  input_tokens: number;
  output_tokens: number;
}

export interface Attribution {
  agent_percentage: number;
  human_percentage: number;
  agent_lines: number;
  total_lines: number;
}

export interface SessionSummary {
  intent: string;
  outcome: string;
}

export interface SessionMetadata {
  checkpoint_id: string;
  session_id: string;
  agent: string;
  is_task: boolean;
  tool_use_id?: string;
  token_usage: TokenUsage;
  initial_attribution?: Attribution;
  summary?: SessionSummary;
}

export interface TaskCheckpoint {
  session_id: string;
  tool_use_id: string;
  agent_id: string;
}

export interface EntireCheckpoint {
  checkpoint_id: string;
  strategy: string;
  branch: string;
  sessions: SessionMetadata[];
  tasks: TaskCheckpoint[];
}

export interface ActiveSession {
  session_id: string;
  agent_type: string;
  first_prompt: string;
  transcript_path: string;
  start_time?: string;
}

// â”€â”€ Commit Detail (combined git + entire) â”€â”€

export interface CommitDetail {
  commit: GitCommit;
  checkpoint?: EntireCheckpoint;
}

// â”€â”€ Message Protocol (webview â†” host) â”€â”€

export type WebviewMessage =
  | { type: "requestDetail"; hash: string }
  | { type: "refresh" };

export type HostMessage =
  | { type: "initialData"; commits: GitCommit[]; activeSessions: ActiveSession[] }
  | { type: "commitDetail"; detail: CommitDetail }
  | { type: "activeSessions"; sessions: ActiveSession[] }
  | { type: "error"; message: string };

