content.content:
  1â†’import type { CommitDetail, SessionMetadata, TaskCheckpoint } from "../src/types";
  2â†’
  3â†’export function clearDetailPanel(container: HTMLElement): void {
  4â†’  container.innerHTML = `<div class="detail-empty">Select a commit to view details</div>`;
  5â†’}
  6â†’
  7â†’export function renderDetailPanel(container: HTMLElement, detail: CommitDetail): void {
  8â†’  container.innerHTML = "";
  9â†’  const { commit, checkpoint } = detail;
 10â†’
 11â†’  // â”€â”€ Git Info â”€â”€
 12â†’  const gitSection = section("Commit");
 13â†’  gitSection.appendChild(field("Hash", commit.hash, true));
 14â†’  gitSection.appendChild(field("Author", `${commit.author} <${commit.authorEmail}>`));
 15â†’  gitSection.appendChild(field("Date", new Date(commit.date).toLocaleString()));
 16â†’  gitSection.appendChild(field("Message", commit.subject));
 17â†’  if (commit.parents.length > 0) {
 18â†’    gitSection.appendChild(field("Parents", commit.parents.map(p => p.slice(0, 7)).join(", "), true));
 19â†’  }
 20â†’  if (commit.refs.length > 0) {
 21â†’    gitSection.appendChild(field("Refs", commit.refs.join(", ")));
 22â†’  }
 23â†’  container.appendChild(gitSection);
 24â†’
 25â†’  if (!checkpoint) {
 26â†’    if (commit.entireCheckpointId) {
 27â†’      const errSection = section("Entire");
 28â†’      const msg = document.createElement("div");
 29â†’      msg.className = "detail-empty";
 30â†’      msg.style.paddingTop = "8px";
 31â†’      msg.textContent = "Checkpoint data could not be loaded";
 32â†’      errSection.appendChild(msg);
 33â†’      container.appendChild(errSection);
 34â†’    }
 35â†’    return;
 36â†’  }
 37â†’
 38â†’  // â”€â”€ Entire Checkpoint â”€â”€
 39â†’  const cpSection = section("Entire Checkpoint");
 40â†’  cpSection.appendChild(field("ID", checkpoint.checkpoint_id, true));
 41â†’  cpSection.appendChild(field("Strategy", checkpoint.strategy));
 42â†’  cpSection.appendChild(field("Branch", checkpoint.branch));
 43â†’  container.appendChild(cpSection);
 44â†’
 45â†’  // â”€â”€ Sessions â”€â”€
 46â†’  for (let i = 0; i < checkpoint.sessions.length; i++) {
 47â†’    const sess = checkpoint.sessions[i];
 48â†’    const sessSection = section(`Session ${i + 1}`);
 49â†’    renderSession(sessSection, sess);
 50â†’    container.appendChild(sessSection);
 51â†’  }
 52â†’
 53â†’  // â”€â”€ Tasks â”€â”€
 54â†’  if (checkpoint.tasks.length > 0) {
 55â†’    const taskSection = section("Sub-agents");
 56â†’    for (const task of checkpoint.tasks) {
 57â†’      taskSection.appendChild(renderTask(task));
 58â†’    }
 59â†’    container.appendChild(taskSection);
 60â†’  }
 61â†’}
 62â†’
 63â†’function renderSession(container: HTMLElement, sess: SessionMetadata): void {
 64â†’  container.appendChild(field("Agent", sess.agent));
 65â†’  container.appendChild(field("Session", sess.session_id, true));
 66â†’  container.appendChild(field("Created", new Date(sess.created_at).toLocaleString()));
 67â†’
 68â†’  if (sess.is_task && sess.tool_use_id) {
 69â†’    container.appendChild(field("Task", sess.tool_use_id, true));
 70â†’  }
 71â†’
 72â†’  // Token usage
 73â†’  if (sess.token_usage) {
 74â†’    const { input_tokens, output_tokens } = sess.token_usage;
 75â†’    const total = input_tokens + output_tokens;
 76â†’    const tokenDiv = document.createElement("div");
 77â†’    tokenDiv.className = "detail-field";
 78â†’    tokenDiv.innerHTML = `
 79â†’      <span class="detail-label">Tokens</span>
 80â†’      <span class="detail-value">
 81â†’        ${formatNumber(input_tokens)} in / ${formatNumber(output_tokens)} out
 82â†’        (${formatNumber(total)} total)
 83â†’      </span>
 84â†’    `;
 85â†’    container.appendChild(tokenDiv);
 86â†’  }
 87â†’
 88â†’  // Attribution
 89â†’  if (sess.initial_attribution) {
 90â†’    const attr = sess.initial_attribution;
 91â†’    const attrContainer = document.createElement("div");
 92â†’    attrContainer.style.marginTop = "6px";
 93â†’
 94â†’    const bar = document.createElement("div");
 95â†’    bar.className = "attribution-bar";
 96â†’
 97â†’    const agentPart = document.createElement("div");
 98â†’    agentPart.className = "attribution-agent";
 99â†’    agentPart.style.width = `${attr.agent_percentage}%`;
100â†’    agentPart.title = `Agent: ${attr.agent_percentage}%`;
101â†’
102â†’    const humanPart = document.createElement("div");
103â†’    humanPart.className = "attribution-human";
104â†’    humanPart.style.width = `${attr.human_percentage}%`;
105â†’    humanPart.title = `Human: ${attr.human_percentage}%`;
106â†’
107â†’    bar.appendChild(agentPart);
108â†’    bar.appendChild(humanPart);
109â†’    attrContainer.appendChild(bar);
110â†’
111â†’    const label = document.createElement("div");
112â†’    label.className = "attribution-label";
113â†’    label.textContent = `Agent ${attr.agent_percentage}% (${attr.agent_lines}/${attr.total_lines} lines)`;
114â†’    attrContainer.appendChild(label);
115â†’
116â†’    container.appendChild(attrContainer);
117â†’  }
118â†’
119â†’  // Summary
120â†’  if (sess.summary) {
121â†’    const summaryDiv = document.createElement("div");
122â†’    summaryDiv.style.marginTop = "8px";
123â†’
124â†’    if (sess.summary.intent) {
125â†’      const intentBlock = document.createElement("div");
126â†’      intentBlock.className = "summary-text";
127â†’      intentBlock.style.marginBottom = "4px";
128â†’      intentBlock.innerHTML = `<strong>Intent:</strong> ${escapeHtml(sess.summary.intent)}`;
129â†’      summaryDiv.appendChild(intentBlock);
130â†’    }
131â†’
132â†’    if (sess.summary.outcome) {
133â†’      const outcomeBlock = document.createElement("div");
